(function ( $ ) {
$.fn.togglePassword = function( options ) {
    var s = $.extend( $.fn.togglePassword.defaults, options ),
    input = $( this );

    $( s.el ).bind( s.ev, function() {
        "password" == $( input ).prop( "type" ) ?
            $( input ).prop( "type", "text" ) :
            $( input ).prop( "type", "password" );
    });
};
$.fn.togglePassword.defaults = {
    ev: "click"
};
}( jQuery ));